// Preprocess image
function preprocessImage(imageElement) {
  return tf.tidy(() => {
    // Convert to tensor
    let tensor = tf.browser.fromPixels(imageElement);

    // Resize to 224x224
    tensor = tf.image.resizeBilinear(tensor, [224, 224]);

    // Normalize to [-1, 1]
    tensor = tensor.div(127.5).sub(1);

    // Add batch dimension
    tensor = tensor.expandDims(0);

    return tensor;
  });
}

// Classify image
async function classifyImage(model, imageElement) {
  const preprocessed = preprocessImage(imageElement);
  const predictions = await model.predict(preprocessed).data();

  // Get top 5 predictions
  const top5 = Array.from(predictions)
    .map((p, i) => ({ probability: p, className: IMAGENET_CLASSES[i] }))
    .sort((a, b) => b.probability - a.probability)
    .slice(0, 5);

  return top5;
}

