const AWSXRay = require('aws-xray-sdk-core');
const AWS = AWSXRay.captureAWS(require('aws-sdk'));

exports.handler = async (event, context) => {
  const segment = AWSXRay.getSegment();

  try {
    // Create subsegment for database operation
    const dbSegment = segment.addNewSubsegment('database-query');
    dbSegment.addAnnotation('table', 'users');
    dbSegment.addMetadata('query', { operation: 'scan' });

    const result = await dynamodb.scan({ TableName: 'users' }).promise();

    dbSegment.close();

    return ResponseBuilder.success(result.Items);
