import { VariableSizeList as List } from 'react-window';

// Dynamic row component
const DynamicRow = ({ index, style, data }) => {
  const item = data.items[index];

  return (
    <div style={style}>
      <div className="dynamic-row">
        <h3>{item.title}</h3>
        <p>{item.content}</p>
        {item.image && <img src={item.image} alt={item.title} />}
      </div>
    </div>
  );
};

// Get item size function
const getItemSize = (index, data) => {
  const item = data.items[index];
  let height = 60; // Base height

  if (item.content) {
    height += Math.ceil(item.content.length / 50) * 20; // Estimate text height
  }

  if (item.image) {
    height += 200; // Image height
  }

  return height;
};

function DynamicVirtualList({ items }) {
  return (
    <List
      height={600}
      itemCount={items.length}
      itemSize={(index) => getItemSize(index, { items })}
      itemData={{ items }}
    >
      {DynamicRow}
    </List>
  );
}
