## Offline Data Management

### IndexedDB for Offline Storage

```javascript
// utils/indexedDB.js
class IndexedDBManager {
  constructor(dbName, version = 1) {
    this.dbName = dbName;
    this.version = version;
    this.db = null;
  }

  async open() {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, this.version);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => {
        this.db = request.result;
        resolve(this.db);
      };

      request.onupgradeneeded = (event) => {
        const db = event.target.result;

        // Create object stores
        if (!db.objectStoreNames.contains('posts')) {
          const postStore = db.createObjectStore('posts', { keyPath: 'id' });
          postStore.createIndex('status', 'status', { unique: false });
          postStore.createIndex('createdAt', 'createdAt', { unique: false });
        }

        if (!db.objectStoreNames.contains('queue')) {
          db.createObjectStore('queue', { keyPath: 'id', autoIncrement: true });
        }
      };
    });
  }

  async add(storeName, data) {
    const transaction = this.db.transaction([storeName], 'readwrite');
    const store = transaction.objectStore(storeName);
    return store.add(data);
  }

  async get(storeName, key) {
    const transaction = this.db.transaction([storeName], 'readonly');
    const store = transaction.objectStore(storeName);
    return store.get(key);
  }

  async getAll(storeName) {
    const transaction = this.db.transaction([storeName], 'readonly');
    const store = transaction.objectStore(storeName);
    return store.getAll();
  }

  async update(storeName, data) {
    const transaction = this.db.transaction([storeName], 'readwrite');
    const store = transaction.objectStore(storeName);
    return store.put(data);
  }

  async delete(storeName, key) {
    const transaction = this.db.transaction([storeName], 'readwrite');
    const store = transaction.objectStore(storeName);
    return store.delete(key);
  }
}

// Usage in React component
import { useEffect, useState } from 'react';

function useOfflineData(storeName) {
  const [data, setData] = useState([]);
  const [db, setDb] = useState(null);

  useEffect(() => {
    const initDB = async () => {
      const dbManager = new IndexedDBManager('MyPWA');
      await dbManager.open();
      setDb(dbManager);

      // Load cached data
      const cachedData = await dbManager.getAll(storeName);
      setData(cachedData);
    };

    initDB();
  }, [storeName]);

  const addItem = async (item) => {
    if (db) {
      await db.add(storeName, item);
      setData(prev => [...prev, item]);
    }
  };

  const updateItem = async (item) => {
    if (db) {
      await db.update(storeName, item);
      setData(prev => prev.map(i => i.id === item.id ? item : i));
    }
  };

  const deleteItem = async (id) => {
    if (db) {
      await db.delete(storeName, id);
      setData(prev => prev.filter(i => i.id !== id));
    }
  };

  return { data, addItem, updateItem, deleteItem };
}
```

