        Span span = tracer.nextSpan().name("create-user").start();

        try (Tracer.SpanInScope ws = tracer.withSpanInScope(span)) {
            span.tag("user.email", request.getEmail());

            // Validate email
            span.annotate("validating-email");
            validateEmail(request.getEmail());

            // Save user
