  max: 5, // Only 5 login attempts per 15 minutes
  skipSuccessfulRequests: true,
  message: 'Too many authentication attempts, please try again later.'
});

// Progressive delay for repeated requests
const speedLimiter = slowDown({
  windowMs: 15 * 60 * 1000,
  delayAfter: 50,
  delayMs: 500,
  maxDelayMs: 20000
});

// Apply middleware
app.use('/api/', basicLimiter);
app.use('/api/auth/', authLimiter);
app.use('/api/', speedLimiter);

// Advanced DDoS protection
class DDoSProtection {
  constructor() {
    this.suspiciousIPs = new Map();
    this.blockedIPs = new Set();
  }

  middleware() {
    return (req, res, next) => {
      const clientIP = req.ip || req.connection.remoteAddress;

      // Check if IP is blocked
      if (this.blockedIPs.has(clientIP)) {
        return res.status(429).json({ error: 'IP blocked due to suspicious activity' });
      }

      // Track request patterns
      this.trackRequest(clientIP, req);

      next();
    };
  }

  trackRequest(ip, req) {
    if (!this.suspiciousIPs.has(ip)) {
      this.suspiciousIPs.set(ip, {
        requests: [],
        patterns: new Set()
      });
    }

    const ipData = this.suspiciousIPs.get(ip);
    ipData.requests.push({
      timestamp: Date.now(),
      path: req.path,
      userAgent: req.get('User-Agent')
    });

    // Clean old requests (older than 1 minute)
    const oneMinuteAgo = Date.now() - 60000;
    ipData.requests = ipData.requests.filter(r => r.timestamp > oneMinuteAgo);

    // Check for suspicious patterns
    if (this.isSuspicious(ipData)) {
      this.blockedIPs.add(ip);
      console.warn(`Blocked suspicious IP: ${ip}`);

      // Unblock after 1 hour
      setTimeout(() => {
        this.blockedIPs.delete(ip);
        this.suspiciousIPs.delete(ip);
      }, 3600000);
    }
  }

  isSuspicious(ipData) {
    const { requests } = ipData;

    // Too many requests in short time
    if (requests.length > 100) {
      return true;
    }

    // Scanning behavior (many different paths)
    const uniquePaths = new Set(requests.map(r => r.path));
    if (uniquePaths.size > 50) {
      return true;
    }

    // Suspicious user agents
    const userAgents = requests.map(r => r.userAgent);
    const suspiciousAgents = userAgents.filter(ua => 
      !ua || ua.includes('bot') || ua.includes('crawler') || ua.length < 10
    );

    if (suspiciousAgents.length > requests.length * 0.5) {
      return true;
    }

    return false;
  }
}
