## Secure File Upload

```javascript
const multer = require('multer');
const path = require('path');
const crypto = require('crypto');
const sharp = require('sharp');

// Secure file upload configuration
const storage = multer.diskStorage({
  destination: (req, file, cb) => {
    // Use user-specific directories
    const userDir = path.join(__dirname, 'uploads', req.user.id.toString());
    fs.mkdirSync(userDir, { recursive: true });
    cb(null, userDir);
  },
  filename: (req, file, cb) => {
    // Generate secure filename
    const uniqueSuffix = crypto.randomBytes(16).toString('hex');
    const extension = path.extname(file.originalname).toLowerCase();
    cb(null, `${uniqueSuffix}${extension}`);
  }
});

// File filter for security
const fileFilter = (req, file, cb) => {
  // Allowed file types
  const allowedTypes = {
    'image/jpeg': '.jpg',
    'image/png': '.png',
    'image/gif': '.gif',
    'image/webp': '.webp'
  };

  // Check MIME type
  if (!allowedTypes[file.mimetype]) {
    return cb(new Error('Invalid file type'), false);
  }

  // Check file extension
  const extension = path.extname(file.originalname).toLowerCase();
  if (allowedTypes[file.mimetype] !== extension) {
    return cb(new Error('File extension does not match MIME type'), false);
  }

  cb(null, true);
};

const upload = multer({
  storage,
  fileFilter,
  limits: {
    fileSize: 5 * 1024 * 1024, // 5MB limit
    files: 5 // Maximum 5 files
  }
});

// Secure upload endpoint
app.post('/api/upload', authenticateUser, upload.array('images', 5), async (req, res) => {
  try {
    const processedFiles = [];

    for (const file of req.files) {
      // Additional security: Re-process images to remove metadata
      const processedPath = path.join(path.dirname(file.path), `processed_${path.basename(file.path)}`);

      await sharp(file.path)
        .resize(1920, 1080, { fit: 'inside', withoutEnlargement: true })
        .jpeg({ quality: 85, progressive: true })
        .toFile(processedPath);

      // Delete original file
      fs.unlinkSync(file.path);

      processedFiles.push({
        filename: path.basename(processedPath),
        size: fs.statSync(processedPath).size,
        mimetype: 'image/jpeg'
      });
    }

    res.json({
      message: 'Files uploaded successfully',
      files: processedFiles
    });
  } catch (error) {
    console.error('Upload error:', error);
    res.status(500).json({ error: 'Upload failed' });
  }
});
