// Feature detection utility
class FeatureDetector {
  static supportsWebP() {
    return new Promise((resolve) => {
      const webP = new Image();
      webP.onload = webP.onerror = () => {
        resolve(webP.height === 2);
      };
      webP.src = 'data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA';
    });
  }

  static supportsIntersectionObserver() {
    return 'IntersectionObserver' in window;
  }

  static supportsServiceWorker() {
    return 'serviceWorker' in navigator;
  }

  static supportsTouchEvents() {
    return 'ontouchstart' in window || navigator.maxTouchPoints > 0;
  }

  static supportsHover() {
    return window.matchMedia('(hover: hover)').matches;
  }

  static getConnectionType() {
    if ('connection' in navigator) {
      return navigator.connection.effectiveType;
    }
    return 'unknown';
  }
}

// Adaptive loading based on connection
class AdaptiveLoader {
  constructor() {
    this.connectionType = FeatureDetector.getConnectionType();
    this.isSlowConnection = ['slow-2g', '2g', '3g'].includes(this.connectionType);
  }

  loadImages() {
    const images = document.querySelectorAll('img[data-src]');

    images.forEach(img => {
      if (this.isSlowConnection) {
        // Load low-quality images for slow connections
        const lowQualitySrc = img.dataset.srcLow;
        if (lowQualitySrc) {
          img.src = lowQualitySrc;
          return;
        }
      }

      // Load full-quality images
      img.src = img.dataset.src;
    });
  }

  loadScripts() {
    if (this.isSlowConnection) {
      // Skip non-essential scripts on slow connections
      console.log('Skipping non-essential scripts due to slow connection');
      return;
    }

    // Load analytics and other non-critical scripts
    this.loadAnalytics();
    this.loadChatWidget();
  }
}
